updated_at
)
VALUES
(
#{ postId }
,#{ authorId }
,#{ password }
,#{ content }
,#{ ipHash }
,#{ createdAt }
,#{ updatedAt }
)

mapper.update:
UPDATE comments
SET content= #{ content },
updated_at= #{ updatedAt }
WHERE comment_id= #{ commentId }
AND author_id= #{ authorId }
AND password= #{ password }

mapper.delete:
DELETE FROM comments
WHERE comment_id= #{ commentId }
AND author_id= #{ authorId }
AND password= #{ password }
