mapper.insert:
INSERT INTO posts
(
board_id,
author_id,
password,
title,
content,
view_count,
ip_hash,
created_at,
updated_at
)
VALUES
(
#{ boardId }
,#{ authorId }
,#{ password }
,#{ title }
,#{ content }
,#{ viewCount }
,#{ ipHash }
,#{ createdAt }
,#{ updatedAt }
)

mapper.update:
UPDATE posts
SET title= #{ title },
content= #{ content },
updated_at= #{ updatedAt }
WHERE post_id= #{ postId }
AND author_id= #{ authorId }
AND password= #{ password }

mapper.delete:
DELETE FROM posts
WHERE post_id= #{ postId }
AND author_id= #{ authorId }
AND password= #{ password }
