mapper.insert:
INSERT INTO post_v3
(
board_id,
author_id,
password,
title,
content,
view_count,
ip_hash,
created_at,
updated_at
)
VALUES
(
#{ boardId }
,#{ authorId }
,#{ password }
,#{ title }
,#{ content }
,#{ viewCount }
,#{ ipHash }
,#{ createdAt }
,#{ updatedAt }
)

INSERT INTO comment_v3
(
post_id,
author_id,
password,
content,
ip_hash,
created_at,
updated_at
)
VALUES
(
#{ postId }
,#{ authorId }
,#{ password }
,#{ content }
,#{ ipHash }
,#{ createdAt }
,#{ updatedAt }
)

mapper.update:
UPDATE post_v3
SET title= #{ title },
content= #{ content },
updated_at= #{ updatedAt }
WHERE post_id= #{ postId }
AND author_id= #{ authorId }
AND password= #{ password }

mapper.delete:
DELETE FROM post_v3
WHERE post_id= #{ postId }
AND author_id= #{ authorId }
AND password= #{ password }

mapper.update:
UPDATE comment_v3
SET content= #{ content },
updated_at= #{ updatedAt }
WHERE comment_id= #{ commentId }
AND author_id= #{ authorId }
AND password= #{ password }

mapper.delete:
DELETE FROM comment_v3
WHERE comment_id= #{ commentId }
AND author_id= #{ authorId }
AND password= #{ password }
